import { set } from './set.js'

/**
 * Create an HTML element
 * @param {string} type 
 * @param {object} obj 
 * @returns {object} html object
 * @example
 * let newElement = cm.create('p', {
 *    innerHTML:'hello world',
 *    style:{
 *      'font-size':'16px';
 *      }
 *    } 
 * );
 * display.appendChild(newElement)
 */

 var create = function create(type, obj, container){

    if(!container && typeof document !== 'undefined') container = document;
    
    let element = container.createElement(type);
    set(element, obj);
    
    return element
  }

  // export default create;
  export { create }
