/**
 * convert integer to hex -- rushed and possibly specific to a couple of recent projects so not properly documented
 * @param {number} input 
 * @returns {string}
 */

 var intToHex = function(input){
    let output=[0,0];
    output[0] = Math.floor(input/16);
    output[1] = input % 16
    for (let index in output){
        output[index] = output[index] > 9 ? String.fromCharCode(output[index] + 55) : output[index];
    }
    return `${output[0]}${output[1]}`
}
