/** 
   * mtof (via Tone)
   * 
   * @param {*} num 
   * @param {*} transpose 
   * @returns {number} frequency in Hz
   */

 var mtof = function(num, transpose = 0){
    num = Tone.Frequency(num, "midi").transpose(transpose)
    return Tone.Frequency(num, "midi").toFrequency()
  }

  // module.exports = mtof;

  export { mtof }