import { create } from './create.js'

/**
 * 
 * @param menu 
 * @param items 
 * @param selected 
 * @param type 
 */

var populate = function(menu, items, selected=false, type='option'){
    items.forEach((x)=>{
        let item = menu.appendChild(create(type,{value:x,innerHTML:x}))
        if(selected!=false)menu.value=selected
    })
}

// module.exports = populate;

export { populate }