/**
 * Set parameters in an HTML element from an object
 * The way that I wish Object.assign would work with styles, dataset etc.
 * Custom values for items that for some reason insist on being set with setAttribute
 * @param {HTMLElement} element 
 * @param {object} obj 
 */

var set = function (element, obj) {
    Object.entries(obj).forEach((x) => {
        let [index, value] = x;
        switch (index) {
            case 'custom':
                Object.entries(value).forEach((y) => element.setAttribute(...y));
                break;
            case 'style':
                Object.entries(value).forEach((y) => element.style[y[0]] = y[1]);
                break;
            case 'dataset':
                Object.entries(value).forEach((y) => element.dataset[y[0]] = y[1]);
                break;
            default:
                element[index] = value;
        }
    })
}

// module.exports = set;

export { set }