
/**
 * Set a value in a JS object tree, with path from an array or string (latter separated by '_')
 * Work in progress. Assign obj as this in case we can attach this to objects in the future..untested.
 * @param {*} path 
 * @param {*} parameter 
 * @param {*} obj 
 */

const setInTree = function(path = [], parameter = 0, obj=this){


    if(typeof path == 'string'){
        path=path.split('_');
    }

    var target = obj;
    path.forEach((x,i,arr)=>{
        if(i<arr.length-1){
            target = target[x]
        }
    })
    console.log(target,parameter)
    target[path[path.length-1]] = parameter;
    
}

// module.exports = setInTree;
export { setInTree }