/**
 * untested
 * @param {boolean} flag 
 */

const toggleScroll = function(flag = false, container=document){
    window.disableScroll = (e)=>{
        e.preventDefault();
    }
    
    if(!flag){
        container.body.addEventListener('scroll',disableScroll)    
    } else {
        container.body.removeEventListener('scroll', disableScroll)
    }
    
}

