const convertTransformStringToMatrix = (transformString) => {
  const transform = new DOMMatrix(transformString);
  return transform;
}

/**
 * Get the X and Y translate values of an element
 * @param {HTMLelement} element 
 * @returns {object}  {x: number, y: number}
 */

const getTranslate = (element) => {
  const transform = getComputedStyle(element).transform;
  const {e,f} = transform == 'none' ? {e:0,f:0} : new DOMMatrix(transform);
  return {x:e, y:f};
  
}

export { getTranslate, convertTransformStringToMatrix }

