/**
 * Set the class of all text elements in an svg element
 * @param {HTMLElement} element 
 * @param {string} action 
 * @param {string} className 
 */

export const setSvgTextClass = function(element, action, className='selected'){
    Array.from(element.children).filter(y=>y.tagName=='svg').forEach(z=>{
            z.querySelectorAll('text').forEach(text=>{
                text.classList[action](className);
            })
    })
}

