function generateSVGFilter(id, filterUnits){
    const filter = document.createElementNS('http://www.w3.org/2000/svg', 'filter');
    filter.setAttribute('id',id);
    filter.setAttribute('filterUnits',filterUnits);
    return filter;
}

function generateFeColorMatrix(id, type, values){
    const feColorMatrix = document.createElementNS('http://www.w3.org/2000/svg', 'feColorMatrix');
    feColorMatrix.setAttribute('id',id);
    feColorMatrix.setAttribute('type',type);
    feColorMatrix.setAttribute('values',values);
    return feColorMatrix;
}

function generateFeComponentTransfer(id){
    const feComponentTransfer = document.createElementNS('http://www.w3.org/2000/svg', 'feComponentTransfer');
    feComponentTransfer.setAttribute('id',id);
    return feComponentTransfer;
}


function generateAnimate(animate){
    const animateElement = document.createElementNS('http://www.w3.org/2000/svg', 'animate');
    Object.keys(animate).forEach(key => {
        animateElement.setAttribute(key, animate[key]);
    })
    return animateElement;
}

export { generateSVGFilter, generateFeColorMatrix, generateFeComponentTransfer, generateAnimate }