import { JSDOM } from 'jsdom';
import fs from 'fs';
import CM from '../index.js'
const dom = new JSDOM(`<!DOCTYPE html><html><head></head><body></body></html>`);
const document = dom.window.document;
const svg = CM.createNS('svg',{
        id:'test'
    }, document)
CM.addAnimation(svg,{
        duration: 1,
        events:{
            0: 'red',
            0.5: 'blue',
            1: 'green'
        },
        custom:{
            id:'testAnimation',
            attributeName: 'fill'
        }
    }, document)
console.log('hello world', svg.outerHTML)