 const fs = require("fs-extra");
 const pug = require('pug');
 const beautify = require('js-beautify').html;


 /**
  * Compiler
  */
 
 let comp = function(){
 
 }
 
 
 let pugFiles = [
     'index'
 ]
 
 let pugSourceFolder = './test_src/pug';
 let pugOutputFolder = './test'
 
 for (let file of pugFiles){
 var html = pug.render(fs.readFileSync(
     `${pugSourceFolder}/${file}.pug`, "utf-8"),
     {filename: `${pugSourceFolder}/${file}.pug`}
     )
   html=beautify(html);
   fs.writeFileSync(`${pugOutputFolder}/${file}.html`, html, "utf-8");
 }