/**
 * Add a style element to the document with the desired classes
 */


const addStyles = function(){
    this.stylesheet = document.createElement('style');
    this.stylesheet.innerHTML = `.${this.classes.detect}{
        pointer-events:auto;
        touch-action:none;
    }
        \n.
    ${this.classes.ignore}{
        pointer-events:none;
        touch-action:none;
    }`;
    
    document.querySelector('head').appendChild(this.stylesheet);

    return this;
}

module.exports = addStyles;