/**
 * Set detect and allow to enable default actions for all children of an element
 * todo: look into assigning prevent default only for certain classes, rather than the other way around?
 * @param {*} element 
 * @param {*} action 
 * @returns {object} 'this' for object chaining
 */

const assignAllowDefault = function(element={}, action='add'){
    element.querySelectorAll('*').forEach(child=>{
        child.classList[action](this.classes.allow)
    })
    
    return this;
}

module.exports = assignAllowDefault;