/**
 * Set event listeners
 * @param {object} target 
 * @returns {object} this for method chaining
 */

const listen = function(target = document){
  for (let item of this.listeners){
      target.addEventListener(item,this,false)
  }
  console.log(`multitouch mapper listening on ${target == document ? 'document' : target.id}...`)
  return this;
}

module.exports = listen;