/**
 * Set actions as dictionary -- callable independently or probably best through setAction
 * @param {*} queryString 
 * @param {*} action 
 * @returns {object} this for method chaining
 */

const setActionsAsDictionary = function(queryString, action = {}){
    Object.entries(action).forEach(([index,item])=>{
        this.actions[queryString][index] = item;
    })
    
    return this;
}

module.exports = setActionsAsDictionary;