/**
 * Remove event listeners
 * @param {object} target 
 * @returns {object} this for method chaining
 */

const unlisten = function(target = document){
    
    for (let item of this.listeners){
        target.removeEventListener(item,this)
    }
    
    console.log(`removed multitouch mapper listener on ${target == document ? 'document' : target.id}...`);

    return this;
}

module.exports = unlisten;