const assign = function(channel, type, pitch, method){

    // temporary fix for "all" on channel 16

    if(channel == 16){
        for(let i=0;i<16;i++){
            this.map[i][type][pitch] = method;        
        }
    } else {
        this.map[channel][type][pitch] = method;
    }
    return this;

}

export default assign;

const assign_new = function(channel, type, pitch, method){

    // temporary fix for "all" on channel 16

    type = type == 'note' ? ['noteOn','noteOff'] : [type];

    if(channel == 16){
        for(let i=0;i<16;i++){
            type.forEach(x=>{
                this.map[i][x][pitch] = method;        
            })
            
        }
    } else {
        this.map[channel][type][pitch] = method;
    }

}