/**
 * 
 * @param {number} channel 
 * @param {symbol} range separate with comma e.g. '0,127'
 * @param {object} method function with arguments (pitch, velocity)
 */

const assignRange = function(channel, range, method){
    if(range=='full' || range=='all') range = '0,127';
    this.map[channel].noteRange[range] = method;
}

export default assignRange;