const init = function(){

    for (let channel = 0; channel < 16; channel++) {
        this.map[channel] = {}
        this.map[channel].cc = []
        this.map[channel].note= [];
        this.map[channel].noteOff= [];
        this.map[channel].noteRange = {};

        for(let index=0; index < 128; index++){
            this.map[channel].cc[index] = function(){};
            this.map[channel].note[index] = function(){};
            this.map[channel].noteOff[index] = function(){};
        }

    }
}

export default init;